// Externals
import phones from './mockPhones'
import categories from 'api/mockCategories'

// В реальном api - fetch.get(`http://google.com/api/phones`)
export const fetchPhones = async () => {
    return new Promise((resolve, reject) => {
        resolve(phones)
    })
}

// В реальном api - fetch.get(`http://google.com/api/phones?offset=${offset}`)
export const loadMorePhones = async ({ offset }) => {
    return new Promise((resolve) => {
        resolve(phones)
    })
}

// В реальном api - fetch.get(`http://google.com/api/phones/${id}`)
export const fetchPhoneById = async (id) => {
    return new Promise((resolve) => {
        const phone = phones.find((item) => item.id === id)
        resolve(phone)
    })
}

// В реальном api - fetch.get(`http://google.com/api/categories`)
export const fetchCategories = async () => {
    return new Promise((resolve, reject) => {
        resolve(categories)
    })
}