import {
  FETCH_PHONES_START, FETCH_PHONES_SUCCESS, FETCH_PHONES_FAILURE,
  LOAD_MORE_PHONES_START, LOAD_MORE_PHONES_SUCCESS, LOAD_MORE_PHONES_FAILURE,
  FETCH_PHONE_BY_ID_START, FETCH_PHONE_BY_ID_SUCCESS, FETCH_PHONE_BY_ID_FAILURE,
  ADD_PHONE_TO_BASKET, SEARCH_PHONE
} from 'actionTypes'


export const fetchPhoneByIdStart = () => ({
  type: FETCH_PHONE_BY_ID_START
})

export const fetchPhoneByIdSuccess = (phone) => ({
  type: FETCH_PHONE_BY_ID_SUCCESS,
  payload: phone
})

export const fetchPhoneByIdFailed = (err) => ({
  type: FETCH_PHONE_BY_ID_FAILURE,
  payload: err,
  error: true
})

//Синхронный экшен, в реальном приложении наверняка был бы асинхронным для запросов на сервер
export const searchPhoneBy = (text) => ({
  type: SEARCH_PHONE,
  payload: text // Передаем текс который мы ищем
})