// Dependencies
import React from 'react'
import { Link } from 'react-router-dom'
import { useSelector } from 'react-redux'

// Externals
import { getPhoneById } from 'services/selectors'

const BasketCart = () => {
  const basket = useSelector((state) => state.basket)
  const phonesObj = useSelector((state) => state.phonesObj)

  // Получение суммы цены
  let totalPrice = 0; // итоговая сумма

  // метод получения массива с объектами в корзине 
  const objecsOnBasket = [...basket].reduce((totalArr, itemId) => {
    totalArr.push(getPhoneById(phonesObj, itemId))

    return totalArr
  }, [])

  // сложение сумм всех объектов в корзине 
  objecsOnBasket.forEach((item) => totalPrice = totalPrice + item.price)

  return (
    <div className='cart'>
      <div className='dropdown'>
        <Link to='/basket' id='dLabel' className='btn btn-inverse btn-block btn-lh'>
          <i className='fa fa-fa-shopping-cart'></i>
          <span>{`${basket.length} item(s) - ${totalPrice}$`}</span>
        </Link>
      </div>
    </div>
  )
}

export default BasketCart;