// Dependencies
import React from 'react'
import { Link } from 'react-router-dom'
import { useSelector } from 'react-redux'

// Externals
import { getTotalBasketPrice } from 'services/selectors'

const BasketCart = () => {
  const basket = useSelector((state) => state.basket)
  const phonesObj = useSelector((state) => state.phonesObj)

  const totalPrice = getTotalBasketPrice(basket, phonesObj)

  return (
    <div className='cart'>
      <div className='dropdown'>
        <Link to='/basket' id='dLabel' className='btn btn-inverse btn-block btn-lh'>
          <i className='fa fa-fa-shopping-cart'></i>
          <span>{`${basket.length} item(s) - ${totalPrice}$`}</span>
        </Link>
      </div>
    </div>
  )
}

export default BasketCart;