// Dependencies
import React, { useEffect } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { Link } from 'react-router-dom'
import * as R from 'ramda'

// Externals
import { fetchPhones } from 'actions'


const Phones = () => {
  const dispatch = useDispatch()
  const phonesObj = useSelector((state) => state.phonesObj)

  const phonesArray = Object.values(phonesObj);

  useEffect(() => {
    dispatch(fetchPhones())
  }, [])


  const renderPhone = (phone, index) => {
    // TODO переписать функцию обрезания слов
    const shortDespiption = `${R.take(60, phone.description)}...`

    return (
      <div className='col-sm-4 col-lg-4 col-md-4 book-list' key={index}>
        <div className='thumbnail'>
          <Link to={`/phones/${phone.id}`}>
            <img className='img-thumbnail' src={phone.image} alt={phone.name} />
          </Link>
          <div className='caption'>
            <h4 className='pull-right'>${phone.price}</h4>
            <h4><Link to={`/phones/${phone.id}`}>{phone.name}</Link></h4>
            <p>{shortDespiption}</p>
            <p className='itemButton'>
              <button className='btn btn-primary'>Buy now!</button>
              <Link to={`/phones/${phone.id}`} className='btn btn-default'>More Info</Link>
            </p>
          </div>
        </div>
      </div>
    )
  }

  return (
    <div className='books row'>
      {
        phonesArray.map((phone, index) => renderPhone(phone, index))
      }
    </div>
  )
}


export default Phones