// Dependencies
import React, { useEffect } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { Link, useParams } from 'react-router-dom'
import * as R from 'ramda'

// Externals
import Layout from 'containers/layout'
import { fetchPhones, fetchCategories, loadMorePhones, addPhoneToBasket } from 'actions'
import { getPhoneById } from 'services/selectors'


const Phones = () => {
  const dispatch = useDispatch()
  const phonesObj = useSelector((state) => state.phonesObj)
  const phonesIds = useSelector((state) => state.phonesPage.ids)
  const phonesSearch = useSelector((state) => state.phonesPage.search)
  const activeCategory = useParams()

  const phonesArray = phonesIds.reduce((totalArr, itemId) => {
    totalArr.push(getPhoneById(phonesObj, itemId))
    return totalArr
  }, [])


  const filtredPhonesArrayForRender = phonesArray.filter((item) => {
    let includesPhoneSearch = item.name.toLocaleLowerCase().includes(phonesSearch)
    let haveActiveCategory = item.categoryId === activeCategory.id

    return activeCategory.id ? includesPhoneSearch && haveActiveCategory : includesPhoneSearch
  })

  useEffect(() => {
    dispatch(fetchPhones())
    dispatch(fetchCategories())
  }, [])

  const renderPhone = (phone, index) => {
    // TODO переписать функцию обрезания слов
    const shortDespiption = `${R.take(60, phone.description)}...`

    return (
      <div className='col-sm-4 col-lg-4 col-md-4 book-list' key={index}>
        <div className='thumbnail'>
          <Link to={`/phones/${phone.id}`}>
            <img className='img-thumbnail' src={phone.image} alt={phone.name} />
          </Link>
          <div className='caption'>
            <h4 className='pull-right'>${phone.price}</h4>
            <h4><Link to={`/phones/${phone.id}`}>{phone.name}</Link></h4>
            <p>{shortDespiption}</p>
            <p className='itemButton'>
              <button className='btn btn-primary' onClick={() => dispatch(addPhoneToBasket(phone.id))}>Buy now!</button>
              <Link to={`/phones/${phone.id}`} className='btn btn-default'>More Info</Link>
            </p>
          </div>
        </div>
      </div>
    )
  }

  return (
    <Layout>
      <div className='books row'>
        {
          filtredPhonesArrayForRender.length === 0 ?
            "Мы ничего не нашли по вашему запросу"
            : filtredPhonesArrayForRender.map((phone, index) => renderPhone(phone, index))
        }
      </div>
      <div className='row'>
        <div className='col-md-12'>
          <button className='pull-right btn btn-primary' onClick={() => dispatch(loadMorePhones())}>Load More</button>
        </div>
      </div>
    </Layout>

  )
}


export default Phones