// Dependencies

// Externals
import { FETCH_PHONES_SUCCESS, LOAD_MORE_PHONES_SUCCESS, SEARCH_PHONE } from "actionTypes"

// Редьюсер для хранения id полученных телефонов
const initialState = {
  ids: [],
  search: ''
}

export default (state = initialState, { type, payload }) => {
  switch (type) {
    case FETCH_PHONES_SUCCESS:
      let newIdsArr = [];
      payload.forEach((item) => newIdsArr.push(item.id))

      return {
        ...state,
        ids: newIdsArr
      }

    case LOAD_MORE_PHONES_SUCCESS:
      let newIds = [] // новый массив для копий новых id из offset
      payload.forEach((item) => newIds.push(item.id)) // Добавляем из перенданного массива ключи в новосозданный массив

      return {
        ...state,
        ids: state.ids.concat(newIds) // увеличиваем количество IDшников
      }

    case SEARCH_PHONE:
      return {
        ...state,
        search: payload
      }

    default:
      return state
  }
}