// Чистые функции для получения данных

// Получаем объект телефона по его id
export const getPhoneById = (state, id) => state[id]

// Получаем длину отредеренного массива IDs для offset пагинации
export const getRenderedPhonesLength = (state) => state.phonesPage.ids.length

export const getCategories = (state) => {
  const newArray = []

  Object.entries(state).forEach(([key, value]) => {
    newArray.push(value);
  });

  return newArray
}

// Получаем сумму в корзине
export const getTotalBasketPrice = (state, objectWithItems) => {
  let totalPrice = 0; // итоговая сумма

  // метод получения массива с объектами в корзине 
  const objecsOnBasket = state.reduce((totalArr, itemId) => {
    totalArr.push(getPhoneById(objectWithItems, itemId))

    return totalArr
  }, [])

  // сложение сумм всех объектов в корзине 
  objecsOnBasket.forEach((item) => totalPrice = totalPrice + item.price)

  return totalPrice
}
